INSERT INTO quotes (id, content, author_id, views) VALUES (399051, 'You can surmount the obstacles in your path if you are determined  courageous and hardworking. ... Do not fear to pioneer  to venture down new paths of endeavor.', 63088, 0), (399052, 'The great man is the man who does a thing for the first time.', 13963, 0), (399053, 'Imposing limitations on yourself is cowardly because it protects you from having to try  and perhaps failing.', 63089, 0), (399054, 'What you can do  or dream you can do  begin it  boldness has genius  power and magic in it.', 62224, 0), (399055, 'We need the courage to start and continue what we should do  and courage to stop what we shouldn''t do.', 20375, 0), (399056, 'Prudence which degenerates into timidity is very seldom the path to safety.', 63090, 0), (399057, 'He that is overcautious will accomplish little.', 62240, 0), (399058, 'We can easily become as much slaves to precaution as we can to fear.', 4976, 0), (399059, 'To the timid and hesitating everything is impossible because it seems so.', 50656, 0), (399060, 'Do not be too timid and squeamish. ... All life is an experiment. The more experiments you make  the better.', 760, 0), (399061, 'Courage is rarely reckless or foolish ... courage usually involves a highly realistic estimate of the odds that must be faced.', 63091, 0), (399062, 'Valour lies just halfway between rashness and cowardice.', 62284, 0), (399063, 'When you''re my size in the pros  fear is a sign that you''re not stupid.', 63092, 0), (399064, 'Keep your fears to yourself  but share your courage with others.', 449, 0), (399065, 'Courage leads starward  fear toward death.', 62237, 0), (399066, 'Go forth to meet the shadowy Future without fear and with a manly heart.', 147, 0), (399067, 'Fate loves the fearless.', 1247, 0), (399068, 'There is no such thing as bravery  only degrees of fear.', 63035, 0), (399069, 'Courage is a peculiar kind of fear.', 63093, 0), (399070, 'Courage is fear that has said its prayer