INSERT INTO quotes (id, content, author_id, views) VALUES (402701, 'Independence may be found in comparative as well as in absolute abundance  I mean where a person contracts his desires within the limits of his fortune.', 63810, 0), (402702, 'One never hugs one''s good luck so affectionately as when listening to the relation of some horrible misfortunes which has overtaken others.', 13963, 0), (402703, 'If you would but exchange places with the other fellow  how much more you could appreciate your own position.', 63811, 0), (402704, 'We should learn  by reflection on the misfortunes of others  that there is nothing singular in those which befall ourselves.', 63812, 0), (402705, 'Double-no  triple-our troubles and we''d still be better off than any other people on earth.', 1727, 0), (402706, 'I wept because I had no shoes  until I saw a man who had no feet.', 63813, 0), (402707, 'When life''s problems seem overwhelming  look around and see what other people are coping with. You may consider yourself fortunate.', 3199, 0), (402708, 'If all misfortunes were laid in one common heap whence everyone must take an equal portion  most people would be contented to take their own and depart.', 1595, 0), (402709, 'Not he who has little  but he who wishes more  is poor.', 62237, 0), (402710, 'The covetous man is always poor.', 63814, 0), (402711, 'He is not poor that hath not much  but he that craves much.', 1348, 0), (402712, 'He is poor who does not feel content.', 18020, 0), (402713, 'I have no riches but my thoughts. Yet these are wealth enough for me.', 297, 0), (402714, 'He who curbs his desires will always be rich enough.', 7630, 0), (402715, 'There is a gigantic difference between earning a great deal of money and being rich.', 5858, 0), (402716, 'That man is richest whose pleasures are the cheapest.', 1049, 0), (402717, 'If you want an accounting of your worth  count your friends.', 63055, 0), (402718, 'I''ve had an exciting life  I married for love and got a little mo