INSERT INTO quotes (id, content, author_id, views) VALUES (403551, 'A true friend is the greatest of all blessings  and that which we take the least care of all to acquire.', 154, 0), (403552, 'A man must eat a peck of salt with his friend before he knows him.', 62284, 0), (403553, 'Have but few friends  though many acquaintances.', 33, 0), (403554, 'One who''s our friend is fond of us  one who''s fond of us isn''t necessarily our friend.', 62237, 0), (403555, 'Make all good men your well-wishers  and then  in the years'' steady sifting  some of them will turn into friends.', 21494, 0), (403556, 'Distrust all those who love you extremely upon a very slight acquaintance and without any viable reason.', 62413, 0), (403557, 'We often choose a friend as we do a mistress  for no particular excellence in themselves  but merely from some circumstance that flatters our self-love.', 8948, 0), (403558, 'How casually and unobservedly we make all our most valued acquaintances.', 760, 0), (403559, 'Be courteous to all  but intimate with few  and let those few be well-tried before you give them your confidence.', 3161, 0), (403560, 'Be slow to fall into friendship  but when thou art in  continue firm and constant.', 1595, 0), (403561, 'How often we find ourselves turning our backs on our actual friends  that we may go and meet their ideal cousins.', 1049, 0), (403562, 'Let him have the key of thy heart  who hath the lock of his own.', 63075, 0), (403563, 'Books and friends should be few but good.', 33, 0), (403564, 'Sudden friendship  sure repentance.', 33, 0), (403565, 'As there are some flowers which you should smell but slightly to extract all that is pleasant in them ... so there are some men with whom a slight acquaintance is quite sufficient to draw out all that is agreeable  a more intimate one would be unsafe and unsatisfactory.', 8764, 0), (403566, 'Acquaintance I would have  but when it depends not on the number  but the choice of friends.', 428, 0), (403567, 'Always se