INSERT INTO quotes (id, content, author_id, views) VALUES (409001, 'I have measured out my life with coffee spoons.', 1067, 0), (409002, 'If there is a sin against life  it consists perhaps not so much in despairing of life as in hoping for another  and in eluding the implacable grandeur of this life.', 532, 0), (409003, 'Life can only be understood backwards  but it must be lived forwards.', 3131, 0), (409004, 'The joy of life is variety  the tenderest love requires to be renewed by intervals of absence.', 2087, 0), (409005, 'O Life! thou art a galling load  Along a rough  a weary road  to wretches such as I.', 1606, 0), (409006, 'I am on the side of the unregenerate who affirm the worth of life as an end in itself  as against the saints who deny it.', 24754, 0), (409007, 'The natural rhythm of human life is routine punctuated by orgies.', 1032, 0), (409008, 'Were the offer made true  I would engage to run again  from beginning to end  the same career of life. All I would ask should be the privilege of an author  to correct  in a second edition  certain errors of the first.', 1077, 0), (409009, 'Life is what happens to us while we are making other plans.', 64537, 0), (409010, 'The man who has no inner life is the slave of his surroundings.', 4754, 0), (409011, 'People in the West are always getting ready to live.', 7652, 0), (409012, 'You''ve got to keep fighting - you''ve got to risk your life every six months to stay alive.', 64538, 0), (409013, 'Unless you can find some sort of loyalty  you cannot find unity and peace in your active living.', 62954, 0), (409014, 'Life  as it is called  is for most of us one long postponement.', 497, 0), (409015, 'Life is a progress from want to want  not from enjoyment to enjoyment.', 2087, 0), (409016, 'Life as we find it is too hard for us  it entails too much pain  too many disappointments  impossible tasks. We cannot do without palliative remedies.', 555, 0), (409017, 'Be intent upon the perfection of the present day.', 2143