INSERT INTO quotes (id, content, author_id, views) VALUES (421551, 'A hard beginning makes a good ending.', 41914, 0), (421552, 'We may our ends by our beginnings know.', 66120, 0), (421553, 'The only joy in the world is to begin.', 1078, 0), (421554, 'Every artist was first an amateur.', 760, 0), (421555, 'The best way to prepare for life is to begin to live.', 4, 0), (421556, 'A schoolboy''s tale  the wonder of an hour!', 62558, 0), (421557, 'Wonder is the basis of worship.', 3061, 0), (421558, 'Men love to wonder and that is the seed of our science.', 760, 0), (421559, 'No wonder can last more than three days.', 8090, 0), (421560, 'Come live in my heart and pay no rent.', 12187, 0), (421561, 'There is a tide in the affairs of women Which  taken at the flood  leads - God knows where.', 62558, 0), (421562, '"Yes " I answered you last night  "No " this morning  sir  I say: Colors seen by candle-light Will not look the same by day.', 62884, 0), (421563, 'And let us mind  faint heart ne''er wan A lady fair.', 62333, 0), (421564, 'Tis enough -  Who listens once will listen twice  Her heart be sure is not of ice  And one refusal no rebuff.', 62558, 0), (421565, 'Never wedding  ever wooing  Still a lovelorn heart pursuing  Read you not the wrong you''re doing In my cheek''s pale hue? All my life with sorrow strewing  Wed or cease to woo.', 7779, 0), (421566, 'If I am not worth the wooing  I surely am not worth the winning.', 147, 0), (421567, 'Thrice happy''s the wooing that''s not long a-doing  So much time is saved in the billing and cooing.', 66121, 0), (421568, 'Why don''t you speak for yourself  John?', 147, 0), (421569, 'Perhaps if you address the lady Most politely  most politely  Flatter and impress the lady Most politely  most politely  Humbly beg and humbly sue  She may deign to look on you.', 6987, 0), (421570, 'The time I''ve lost in wooing  In watching and pursuing The light that lies In woman''s eyes  Has been my heart''s undoing.', 1282, 0), (421571, 'They