INSERT INTO quotes (id, content, author_id, views) VALUES (463301, 'So, if I give any credit, besides giving all credit to God, for being totally healthy, it''s just my workout and how much torture I put my body through in the offseason.', 45107, 0), (463302, 'God loves you unconditionally, as you are and not as you should be, because nobody is as they should be.', 575, 0), (463303, 'What ever disunites man from God, also disunites man from man.', 2026, 0), (463304, 'Whatever comes from God is impossible for a man to turn back.', 1863, 0), (463305, 'Young man, young man, your arm''s too short to box with God.', 12431, 0), (463306, 'A thick skin is a gift from God.', 62427, 0), (463307, 'Reason is God''s crowning gift to man.', 139, 0), (463308, 'Only God can look at somebody''s heart.', 3956, 0), (463309, 'Each one of us is an outlet to God and an inlet to God.', 5203, 0), (463310, 'For it pleased God, after he had made all things by the word of his power, to create man after his own image.', 46036, 0), (463311, 'The Bible is a revelation of the mind and will of God to men. Therein we may learn, what God is.', 73515, 0), (463312, 'The condition upon which God hath given liberty to man is eternal vigilance.', 16691, 0), (463313, 'God, as Truth, has been for me a treasure beyond price. May He be so to every one of us.', 32, 0), (463314, 'If God had wanted me otherwise, He would have created me otherwise.', 282, 0), (463315, 'All men are born with a nose and five fingers, but no one is born with a knowledge of God.', 808, 0), (463316, 'It is the heart which perceives God and not the reason. That is what faith is: God perceived by the heart, not by the reason.', 144, 0), (463317, 'After all, God is God because he remembers.', 10, 0), (463318, 'In myself I am nothing. It all comes from God and the Virgin Mary.', 63581, 0), (463319, 'I''ve said all along that God is in control.', 4363, 0), (463320, 'Since God knows our future, our personalities, and our capacity to listen